sys_script_include.script:
var EstimateUtil = Class.create();

EstimateUtil.answers = [0, 1, 2, 3, 5, 8, 13, 20, 40, 100];
EstimateUtil.story = '';
EstimateUtil.user = '';

EstimateUtil.prototype = {
    initialize: function(story, user) {
        this.story = story;
        this.user = user;
    },

	validAnswers: function() {
		return this.answers;
	},

    validStory: function() {
        var grStory = new GlideRecord("rm_story");
        grStory.addEncodedQuery("sys_id=" + this.story + "^state=-6");
        grStory.query();
        if (grStory.next()) {
            return true;
        } else {
            return false;
        }
    },

    getEstimate: function() {
        var response = {
            sysid: "",
            value: 0
        };
        var grM2m = this._getEstimateRecord();
        if (grM2m.next()) {
            response.sysid = grM2m.getUniqueValue();
            response.value = grM2m.estimate.toString();
        }
        return response;
    },

    setEstimate: function(estimate) {
        var grM2m = this._getEstimateRecord();
        if (!grM2m.next()) {
            grM2m.initialize();
            grM2m.story = this.story;
            grM2m.user = this.user;
        }
        grM2m.estimate = parseInt(estimate, 10);
		grM2m.update();
    },

    _getEstimateRecord: function() {
        var grM2m = new GlideRecord('x_550467_estimate_m2m_users_stories');
        grM2m.addEncodedQuery("story=" + this.story + "^user=" + this.user);
        grM2m.query();
		return grM2m;
    },

    type: 'EstimateUtil'
};
